    private void jButton3ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButton3ActionPerformed
        // Инф. о классе
        Descr descr = this.getClass().getAnnotation(Descr.class);
        System.out.println("Класс: num = " + descr.num() + ", text = " + descr.text());
        jTextArea1.append("Класс: num = " + descr.num() + " text = " + descr.text());
    }//GEN-LAST:event_jButton3ActionPerformed

    private void jButton1ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButton1ActionPerformed
        // Получаем информацию об аннотации
        Descr descr;
        try {
            Field field = this.getClass().getDeclaredField("jButton1");
            descr = field.getAnnotation(Descr.class);
            System.out.println("jButton1: num = " + descr.num() + " text = " + descr.text());
            jTextArea1.append("\njButton1: num = " + descr.num() + " text = " + descr.text());
        } catch (NoSuchFieldException ex) {
            System.out.println("ош.");
            System.out.println(ex.getMessage());
        }
    }//GEN-LAST:event_jButton1ActionPerformed

