    private void jButton4ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButton4ActionPerformed
        Method method;
        Descr descr;
        try {
            method = this.getClass().getDeclaredMethod("M1", null);
            descr = method.getAnnotation(Descr.class);
            System.out.println("Метод: num = " + descr.num() + " text = " + descr.text());
            jTextArea1.append("\nМетод: num = " + descr.num() + " text = " + descr.text());
        } catch (NoSuchMethodException f) {
            System.out.println("ош.");
        }
    }//GEN-LAST:event_jButton4ActionPerformed

    private void jButton6ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButton6ActionPerformed
        Method method;
        Descr descr;
        try {   // Для метода с 2-мя параметрами
            method = this.getClass().getDeclaredMethod("M2", String.class, String.class);
            descr = method.getAnnotation(Descr.class);
            System.out.println("Метод: num = " + descr.num() + " text = " + descr.text());
            jTextArea1.append("\nМетод: num = " + descr.num() + " text = " + descr.text());
        } catch (NoSuchMethodException f) {
            System.out.println("ош.");
        }
    }//GEN-LAST:event_jButton6ActionPerformed

    private void jButton5ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButton5ActionPerformed
//        Количество параметров в методе
        System.out.println("Кол-во параметров: " + this.getMethodC1("111", Integer.class, String.class, Object.class));
        System.out.println("Кол-во параметров: " + this.getMethodInt("111", 1, 2, 3, 4));
        jTextArea1.append("\nКол-во параметров: " + this.getMethodInt("111", 1, 2, 3, 4));
    }//GEN-LAST:event_jButton5ActionPerformed
    
