    private Integer getMethodC1(String name, Class<?>... p) {
//        Переменное число параметров для generic-типа
        return p.length;
    }
    
    private Integer getMethodInt(String name, Integer... p) {
//        p - массив параметров
        return p.length;
    }        
    
